var app = angular.module('myApp',['ngRoute']);
app.config(function($routeProvider) {
    $routeProvider
	.when('/',{
	templateUrl:'loginpage/login2.html',
	controller :'myCtrl'
})
	.when('/nxt', {
        templateUrl : 'loginpage/welcome1.html',
		controller:  'myCtrl'
		})
		
	.when('/Reg', {
        templateUrl : 'registerpage/registerform.html',
		controller  : 'simpleCtrl'
	})
	.when('/login', {
        templateUrl : 'loginpage/login2.html',
		controller:'myCtrl'
	})
	.when('/page', {
        templateUrl : 'registerpage/succ.html',
	    controller:'simpleCtrl'
		})
		
			.otherwise({
		redirectTo :'loginpage/login2'
	});	
});
	app.controller('myCtrl',function($scope,$location){
		$scope.cp=function($scope){
			$location.path("/nxt");
		}
	})
	app.controller('simpleCtrl',function($scope,$location){
		$scope.reg=function($scope){
			$location.path("/page");
		}
	})
	
	
app.controller('DropCtrl1', function($scope, $http) {
$http.get('http://localhost:3000/mainmenu2')
.then(function(response){
        $scope.y=response.data;            
        });
});
app.controller('DropCtrl', function(){
	$(document).ready(function () {
    var nav = function () {
        $('.gw-nav > li > a').click(function () {
            var gw_nav = $('.gw-nav');
            gw_nav.find('li').removeClass('active');
            $('.gw-nav > li > ul > li').removeClass('active');

            var checkElement = $(this).parent();
            var ulDom = checkElement.find('.gw-submenu')[0];

            if (ulDom == undefined) {
                checkElement.addClass('active');
                $('.gw-nav').find('li').find('ul:visible').slideUp();
                return;
            }
            if (ulDom.style.display != 'block') {
                gw_nav.find('li').find('ul:visible').slideUp();
                gw_nav.find('li.init-arrow-up').removeClass('init-arrow-up').addClass('arrow-down');
                gw_nav.find('li.arrow-up').removeClass('arrow-up').addClass('arrow-down');
                checkElement.removeClass('init-arrow-down');
                checkElement.removeClass('arrow-down');
                checkElement.addClass('arrow-up');
                checkElement.addClass('active');
                checkElement.find('ul').slideDown(300);
            } else {
                checkElement.removeClass('init-arrow-up');
                checkElement.removeClass('arrow-up');
                checkElement.removeClass('active');
                checkElement.addClass('arrow-down');
                checkElement.find('ul').slideUp(300);

            }
        });
       };
    nav();
});
});                                         


	